export const normalizePath = (path, prefix) => {
    path = path.replace(prefix, '');
    if (path.startsWith('//')) {
        path = path.replace('//', '/');
    }
    if (!path.startsWith('/')) {
        path = `/${path}`;
    }
    return path;
};
